import React from 'react';

class Minesweeper extends React.Component {

    constructor(props) {
        super(props);

        // Read in props
        this.state = {
            size: props.size,
            mines: props.mines,
            map: []
        };

        // Create a new mine field
        this.createNewMap();
    }

    createNewMap () {
        var _map = this.state.map;

        // Create the blank map
        for(var x = 0; x < this.state.size; x++) {
            _map[x] = [];

            for (var y = 0; y < this.state.size; y++) {
                _map[x][y] = {
                    "flag": false,                    
                    "open": false,
                    "bomb": false
                }
            }
        }


        // Randomize mine locations
        for(var i = 0; i < this.state.mines; i++) {
            _map[Math.floor(Math.random() * this.state.size)][Math.floor(Math.random() * this.state.size)].bomb = true;
        }

        // Store it
        this.setState({map: _map});
    }

    render() {
        console.log(this.state.map)
        return (<h1>{this.state.map.toString()}</h1>);
    }
}

export default Minesweeper;
