import React from 'react';

class Minesweeper extends React.Component {

    constructor(props) {
        super(props);

        // Read in props
        this.state = {
            size: props.size,
            mines: props.mines,
            map: []
        };

        // Create a new mine field
        this.createNewMap();
    }

    createNewMap() {
        var _map = this.state.map;

        // Create the blank map
        for (var x = 0; x < this.state.size; x++) {
            _map[x] = [];

            for (var y = 0; y < this.state.size; y++) {
                _map[x][y] = {
                    "flag": false,
                    "open": false,
                    "mine": false
                }
            }
        }

        // Randomize mine locations
        for (var i = 0; i < this.state.mines; i++) {
            _map[Math.floor(Math.random() * this.state.size)][Math.floor(Math.random() * this.state.size)].mine = true;
        }

        // Store it
        this.setState({ map: _map });
    }

    leftClickHandler(x, y) {
        var _map = this.state.map;
        _map[x][y].open = true;
        this.setState({map:_map}, () => {
            if(this.state[x][y].mine) {
                console.log("Boom");
            }
        });
    }

    rightClickHandler(x, y) {
        var _map = this.state.map;
        _map[x][y].flag = true;
        this.setState({map:_map});
    }

    determineContent(x, y) {
        if(this.state.map[x][y].open) {
            return "0"
        } else if (this.state.map[x][y].flag) {
            return "☑";
        } else {
            return "☐"
        }
    }

    render() {
        return (
            <table>
                <thead>
                </thead>
                <tbody>
                    {Array.from(Array(this.state.size).keys()).map(x => {
                        return (
                            <td key={x}>
                                {Array.from(Array(this.state.size).keys()).map(y => {
                                    return (
                                        <tr onClick={()=>this.leftClickHandler(x, y)}
                                            onContextMenu={()=>this.rightClickHandler(x,y)}
                                            key={y}>
                                                {this.determineContent(x, y)}
                                        </tr>
                                    );
                                })}
                            </td>
                        )
                    })}
                </tbody>
            </table>
        );
    }
}

export default Minesweeper;
