import React from 'react';

class Minesweeper extends React.Component {

    constructor(props) {
        super(props);

        // Read in props
        this.state = {
            size: props.size,
            mines: props.mines,
            map: []
        };

        // Create a new mine field
        this.createNewMap();
    }

    createNewMap() {
        var _map = this.state.map;

        // Create the blank map
        for (var x = 0; x < this.state.size; x++) {
            _map[x] = [];

            for (var y = 0; y < this.state.size; y++) {
                _map[x][y] = {
                    "flag": false,
                    "open": false,
                    "mine": false
                }
            }
        }

        // Randomize mine locations
        for (var i = 0; i < this.state.mines; i++) {
            _map[Math.floor(Math.random() * this.state.size)][Math.floor(Math.random() * this.state.size)].mine = true;
        }

        // Store it
        this.setState({ map: _map });
    }

    leftClickHandler(x, y) {

    }

    rightClickHandler(x, y) {

    }

    render() {
        return (
            <table>
                <thead>
                </thead>
                <tbody>
                    {this.state.map.map((x, xi) => {
                        return (
                            <td key={xi}>
                                {x.map((y, yi) => {
                                    return (
                                        <tr onClock={()=>this.leftClickHandler(xi, yi)} key={yi}>{yi+xi}</tr>
                                    );
                                })}
                            </td>
                        )
                    })}
                </tbody>
            </table>
        );
    }
}

export default Minesweeper;
