import React from 'react';

class Minesweeper extends React.Component {

    constructor(props) {
        super(props);

        // Read in props
        this.state = {
            size: props.size,
            mines: props.mines,
            map: null
        };

        // Create a new mine field
        this.setState({map: this.createNewMap()});
    }

    createNewMap () {
        var map = new Array(this.state.size);

        for(var y = 0; y < this.state.size; y++) {
            map[y] = new Array(this.state.size);

            for (var x = 0; x < this.state.size; x++) {
                map[y][x] = {
                    "x": x,
                    "y": y
                }
            }
        }

        return map;
    }

    render() {
        return (<h1>{this.state.map}</h1>);
    }
}

export default Minesweeper;
