import React from 'react';

class Minesweeper extends React.Component {

    constructor(props) {
        super(props);

        // Read in props
        this.state = {
            size: props.size,
            mines: props.mines,
            map: new Array(props.size)
        };

        // Create a new mine field
        this.createNewMap();
    }

    createNewMap () {
        for(var x = 0; x < this.state.size; x++) {
            this.state.map[x] = new Array(this.state.size);

            for (var y = 0; y < this.state.size; y++) {
                this.state.map[x][y] = {
                    "x": x,
                    "y": y
                }
            }
        }
    }

    render() {
        return (<h1>{this.state.map}</h1>);
    }
}

export default Minesweeper;
