import React from 'react';

class Minesweeper extends React.Component {

    constructor(props) {
        super(props);

        // Read in props
        this.state = {
            size: props.size,
            mines: props.mines,
            map: []
        };

        // Create a new mine field
        this.createNewMap();
    }

    createNewMap () {
        var _map = new Array(this.state.size);

        for(var x = 0; x < this.state.size; x++) {
            _map[x] = new Array(this.state.size);

            for (var y = 0; y < this.state.size; y++) {
                _map[x][y] = {
                    "x": x,
                    "y": y
                }
            }
        }

        this.setState({map: _map});
        this.forceUpdate();
    }

    render() {
        console.log(this.state.map)
        return (<h1>{this.state.map}</h1>);
    }
}

export default Minesweeper;
